    // 使用fastrandn随机算法，设置pollorder数组，后面会根据这个数组进行循环，以达到随机case
    norder := 0
    for i := range scases {
        cas := &scases[i]

        // 从轮询和锁定订单中忽略没有channel的情况。
        if cas.c == nil {
            cas.elem = nil // allow GC
            continue
        }

        j := fastrandn(uint32(norder + 1))
        pollorder[norder] = pollorder[j]
        pollorder[j] = uint16(i)
        norder++
